    .worksheet {
        overflow-x: auto;
    }
    
    .worksheet .ui-layout-west {
        margin-right: 5px;
        display: inline-block;
        height: 100%;
        width: 300px !important;
    }

    .worksheet .ui-layout-center {
        margin-left: 5px;
        margin-right: 5px;
        display: inline-block;
        height: 100%;
        width: calc(100% -355px) !important;
    }

    .worksheet .ui-layout-right {
        height: 100%;
        width: 100% !important;
    }

    .worksheet .ui-layout-east {
        margin-left: 5px;
        width: 355px !important;
        height: 100%;
        display: flex;
        flex-direction: column;
    }
}

@media (max-width: 705px) {
    .worksheet .ui-layout-west {
        margin-right: 5px;
        display: inline-block;
        height: 100%;
        width: 100% !important;
    }

    .worksheet .ui-layout-center {
        margin-left: 5px;
        margin-right: 5px;
        display: inline-block;
        height: 100%;
        width: 100% !important;
    }

    .worksheet .ui-layout-right {
        height: 100%;
        width: 100% !important;
    }

    .worksheet .ui-layout-east {
        margin-left: 5px;
        width: 100% !important;
        height: 100%;
        display: flex;
        flex-direction: column;
        z-index: 100;
        background-color: #FFFFFF;
    }
}

.worksheet .oj-tabs {
    border-radius: 2px;
}

